import React, { Component } from 'react';
import Header from "./Header";
import MessageList from './MessageList';
import MessageInput from './MessageInput';

class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      roomInform:{
        roomName: 'My chat',
        numberUsers: 23,
        numberMessages: 53,
        lastMessageDate:"14.28"
      },
      messages:[""],
      idUser:"61325"
    }
  }

  render() {
    const data = this.state;
    return (
      <div className="pad-lr_chat">
        <Header 
          roomInfo = {data.roomInform}
        />
        <MessageList
          messages={data.message}
          myId={data.idUser}
        />
        <MessageInput/>
      </div>
    );
  }
}

export default Chat;
