import React from "react";
import Message from "./Message";

function MessageList(props) {
    const timeNow = new Date();
  return (
    <div className="full-size flex-drctCol">
      {props.messages.map((data) => {
          const timeMessage = new Date(data.createdAt).toLocaleDateString();
          if(timeNow !== timeMessage){
              timeNow = timeMessage
              console.log(new Date().toLocaleDateString(), new Date(data.createdAt).toLocaleDateString())
          }
        return (
          <Message
            data={data}
            myId={props.myId}
            deleteMessage={props.deleteMessage}
            key={data.id}
          />
        );
      })}
    </div>
  );
}

export default MessageList;
