import React, { Component } from "react";

class MessageInput extends Component {
    constructor(props) {
        super(props);
        this.state={
            "id": "80e00b40-1b8f-11e8-9629-c7eca82aa7b5",
            "text": "Tells exactly what happened.",
            "createdAt": "2020-07-16T19:48:42.481Z",
            "editedAt": "2020-07-16T19:48:47.481Z"
        }
    }

  render() {
    return (
      <div>
        <div className="pad-lr_30px chat-msg_submit flex flex-vertCntr">
          <input
            className="chat-msg_input"
            type="text"
            name="message"
            placeholder="Message"
          />
          <button className="chat-msg_button">Send</button>
        </div>
      </div>
    );
  }
}

export default MessageInput;
