import { ADD_NEW_MESSAGE, FETCHED_MESSAGES, DELETE_MESSAGE, SHOW_EDIT, HIDE_EDIT } from "../actions/actionsType";
const initialState = {
  messages: [],
  myInform: {
    userId: "61325",
    avatar:
      "https://resizing.flixster.com/EVAkglctn7E9B0hVKJrueplabuQ=/220x196/v1.cjs0NjYwNjtqOzE4NDk1OzEyMDA7MjIwOzE5Ng",
    user: "Yaroslav",
  },
  edtiMsg: {
    id: "",
    editMsg: false,
    text: "",
  },
};

export function messageReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_NEW_MESSAGE:
        const message = {
            id: action.payload.id,
            userId: state.myInform.userId,
            avatar: state.myInform.avatar,
            user: state.myInform.user,
            text: action.payload.text,
            createdAt: action.payload.createdAt,
            editedAt: ""
        };
      return {...state, messages:[...state.messages, message]};

    case FETCHED_MESSAGES:
      return {...state, messages:[...state.messages, ...action.payload]};

    case DELETE_MESSAGE:
      return {...state, messages: state.messages.filter((message) => message.id !== action.payload)};
    case SHOW_EDIT:
      return {...state, editMsg: {id:action.payload.id, editMsg:true, text:action.payload.text}};
    case HIDE_EDIT:
      return {...state, editMsg: {id:"", editMsg:false, text:""}}

    default:
      return state;
  }
}
