import React from 'react';
import Avatar from '../helpers/Avatar';
import Like from '../helpers/Like';
import Delete from '../helpers/Delete';
import Edit from '../helpers/Edit';


function Message(props) {
    return (
        <div>
            {
            props.data.userId === props.myId
             ?
                <div className=" flex flex-vertCntr pos_right marg-t_20px pad-r_10px">
                    <div className="chat-msg chat-msg_right">
                        <div className="font-mid">
                            {props.data.text}
                        </div>
                        <div className="msg-date flex flex-spcbtwn">
                            {props.data.createdAt}
                            <div className="flex msg-iconDiv">
                                <Edit/>
                                <Delete/>
                            </div>
                        </div>
                    </div>
                    <Avatar src={props.data.image}/>
                </div>
             :
                <div className=" flex flex-vertCntr pos_left marg-t_20px pad-l_10px">
                    <Avatar src={props.data.avatar}/>
                    <div className="chat-msg chat-msg_left">
                        <div className="font-small">
                            {props.data.user}
                        </div>
                        <div className="font-mid">
                            {props.data.text}
                        </div>
                        <div className="msg-date">
                            {props.data.createdAt}
                        </div>
                        <div className="msg-iconDiv">
                            <label>
                                <input type="checkbox"/>
                                <Like/>
                            </label>
                        </div>
                    </div>
                </div>
            }
        </div>
    )
}

export default Message
