import React, { Component } from 'react';
import { connect } from "react-redux";
import {hideEdit} from '../actions/messageActions'; 

class Modal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      editedMsg: {
        id: "",
        text: "",
      },
    };
  }
  inputHandlerNew = (e) => {
    const text = e.target.value.trim();
    const id = uuidv4();
    const date = new Date();
    this.setState({
      editedMsg: {
        text: text,
        id: id,
        createdAt: date.toString(),
      },
    });
  };

  sendText = () => {
    if (this.state.msg.text) {
      this.props.addMessage(this.state.msg);
      this.state.msg.text = "";
    }
  };
  render() {
    if (this.props.editMsg) {
    if (this.props.editMsg.editMsg) {
      return (
        <div className="modal flex flex-drctCol flex-spcarrnd flex-horCntr">
          <h2>Edit message</h2>
          <input
            className="editMsg-input"
            type="textarea"
            name="Edit message"
            placeholder="Edit your message"
          />
          <div>
            <button className="editMsg-button" type="button">
              Ok
            </button>
            <button className="editMsg-button" type="button" onClick={this.props.hideEdit}>
              Cancel
            </button>
          </div>
        </div>
      );
    } else {
      return null;
    }
  }else{
    return null;
  }
  }
}

const mapStateToProps = (state) => {
  return {
    editMsg: state.chat.editMsg,
  };
};
const mapDispatchToProps = {
  hideEdit
};

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
