import React, { Component } from 'react';
import Header from "./Header";
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import Spiner from '../animatedElem/Spiner';

class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      roomInform:{
        roomName: 'My chat',
        numberUsers: 23,
        numberMessages: 53,
        lastMessageDate:"14.28"
      },
      messages:[
        {userId:"1231",name:"username",msg:"MESSAGE",date:"14.5",image:"https://icon-library.com/images/avatar-icon-images/avatar-icon-images-4.jpg"},
        {userId:"61325",name:"username1",msg:"MESSAGE",date:"14.5",image:"https://icon-library.com/images/avatar-icon-images/avatar-icon-images-4.jpg"},
        {userId:"12131",name:"username1",msg:"MESSAGE",date:"14.5",image:"https://icon-library.com/images/avatar-icon-images/avatar-icon-images-4.jpg"}
        ],
      idUser:"61325"
    }
  }

  render() {
    const data = this.state;
    return (
      <div className="pad-lr_chat">
        <Header 
          roomInfo = {data.roomInform}
        />
        <div className="chat-messageWndw marg-t_20px">
          {data.messages.length !== 0
            ?
              <MessageList
                messages={data.messages}
                myId={data.idUser}
              />
            :
              <Spiner/>
          }
        </div>
        <MessageInput/>
      </div>
    );
  }
}

export default Chat;
