import React, { Component } from 'react';
import Header from "./Header";
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import Spiner from '../animatedElem/Spiner';

class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      roomInform:{
        roomName: 'My chat',
        numberUsers: 23,
        numberMessages: 53,
        lastMessageDate:"14.28"
      },
      messages:[{idUser:"2134",msg:"wqeh"}],
      idUser:"61325"
    }
  }

  render() {
    const data = this.state;
    return (
      <div className="pad-lr_chat">
        <Header 
          roomInfo = {data.roomInform}
        />
        {data.messages
          ?
            <MessageList
              messages={data.messages}
              myId={data.idUser}
            />
          :
            <Spiner/>
        }
        <MessageInput/>
      </div>
    );
  }
}

export default Chat;
