  if (props.prevDate !== timeMessage) {
    console.log(month,date);
    return(
      <div className="flex flex-horCntr pad-t_30px">
        <div className="flex flex-centered date-block">
          <h3>{`${date} ${month}`}</h3>
        </div>
      </div>
    );
