import { ADD_NEW_MESSAGE } from "../actions/actionsType";
const initialState = {
  messages: [],
  myInform: {
    userId: "61325",
    avatar:
      "https://resizing.flixster.com/EVAkglctn7E9B0hVKJrueplabuQ=/220x196/v1.cjs0NjYwNjtqOzE4NDk1OzEyMDA7MjIwOzE5Ng",
    user: "Yaroslav",
  },
  edtMsg: {
    id: "",
    editMsg: false,
    text: "",
  },
};

export function messageReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_NEW_MESSAGE:
        const myInform = state.myInform;
        const message = {
            id: action.payload.id,
            userId: myInform.userId,
            avatar: myInform.avatar,
            user: myInform.user,
            text: action.payload.text,
            createdAt: action.payload.createdAt,
            editedAt: ""
        };
      return [...state.messages,message];

    default:
      return state;
  }
}
