import {combineReducers} from 'redux';
import {headerReducer} from './headerReducer';
import {messageReducer} from './messageReducer';

export function mainReducer(state = initialState, action) {
  switch (action.type) {
    case 'ADD_TODO':
      return state.concat([action.text])
    default:
      return state
  }
}
