import React, { Component } from 'react';
import { connect } from "react-redux";
import {hideEdit, editMessage} from '../actions/messageActions'; 

class Modal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      editedMsg: {
        id: "",
        text: "",
      },
    };
  }
  inputHandlerEdit = (e) =>{
    const text = e.target.value;
    this.setState({
      editedMsg:{
        id:this.props.edtMsg.id,
        text:text
      }
    });
  };

  editText = () => {
    this.props.editMessage(this.state.editedMsg);
    this.state.editedMsg.text = "";
  };
  render() {
    if (this.props.editMsg) {
    if (this.props.editMsg.editMsg) {
      return (
        <div className="modal flex flex-drctCol flex-spcarrnd flex-horCntr">
          <h2>Edit message</h2>
          <input
            className="editMsg-input"
            type="textarea"
            name="Edit message"
            placeholder="Edit your message"
          />
          <div>
            <button className="editMsg-button" type="button">
              Ok
            </button>
            <button className="editMsg-button" type="button" onClick={this.props.hideEdit}>
              Cancel
            </button>
          </div>
        </div>
      );
    } else {
      return null;
    }
  }else{
    return null;
  }
  }
}

const mapStateToProps = (state) => {
  return {
    editMsg: state.chat.editMsg,
  };
};
const mapDispatchToProps = {
  hideEdit,
  editMessage
};

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
