import React from "react";
import { connect } from "react-redux";
import {hideEdit} from '../actions/messageActions'; 

function Modal(props) {
  if (props.editMsg) {
    if (props.editMsg.editMsg) {
      return (
        <div className="modal flex flex-drctCol flex-spcarrnd flex-horCntr">
          <h2>Edit message</h2>
          <input
            className="editMsg-input"
            type="textarea"
            name="Edit message"
            placeholder="Edit your message"
          />
          <div>
            <button className="editMsg-button" type="button">
              Ok
            </button>
            <button className="editMsg-button" type="button" onClick={props.hideEdit}>
              Cancel
            </button>
          </div>
        </div>
      );
    } else {
      return null;
    }
  }else{
    return null;
  }
}

const mapStateToProps = (state) => {
  return {
    editMsg: state.chat.editMsg,
  };
};
const mapDispatchToProps = {
  hideEdit
};

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
