import React from "react";

function Modal() {
  return (
    <div className="modal flex-drctCol flex-spcarrnd flex-horCntr display-none">
      <h2>Edit message</h2>
      <input
      className="editMsg-input"
        type="textarea"
        name="Edit message"
        placeholder="Edit your message"
      />
      <div>
          <button className="editMsg-button" type="button">Ok</button>
        <button className="editMsg-button" type="button">Cancel</button>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    messages: state.chat.messages,
    myInform: state.chat.myInform,
    headerInform: state.header,
  };
};
const mapDispatchToProps = {
  addFetchedMessage,
  deleteMessage,
  showEdit
};

export default Modal;
