import React from 'react';
import Avatar from '../helpers/Avatar';

function Message(props) {
    return (
        <div>
            {
            props.data.userId === props.myId
             ?
                <div className="wdth_100prc flex flex-vertCntr float_right">
                    <div className="chat-msg float_right">
                        RIGHT{props.data.msg}
                    </div>
                    <Avatar src={props.data.image}/>
                </div>
             :
                <div className="wdth_100prc flex flex-vertCntr float_left">
                    <Avatar src={props.data.image}/>
                    <div className="chat-msg float_left">
                        LEFT{props.data.msg}
                    </div>
                </div>
            }
        </div>
    )
}

export default Message
