import React, { Component } from 'react';
import Header from "./Header";
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import Spiner from '../animatedElem/Spiner';

class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      roomInform:{
        roomName: 'My chat',
        numberUsers: 23,
        numberMessages: 53,
        lastMessageDate:"14.28"
      },
      messages:[
        {
            "id": "80f08600-1b8f-11e8-9629-c7eca82aa7bd",
            "userId": "9e243930-83c9-11e9-8e0c-8f1a686f4ce4",
            "avatar": "https://resizing.flixster.com/kr0IphfLGZqni5JOWDS2P1-zod4=/280x250/v1.cjs0OTQ2NztqOzE4NDk1OzEyMDA7MjgwOzI1MA",
            "user": "Ruth",
            "text": "I don’t *** understand. It's the Panama accounts",
            "createdAt": "2020-07-16T19:48:12.936Z",
            "editedAt": ""
        }, {
            "id": "80e00b40-1b8f-11e8-9629-c7eca82aa7bd",
            "userId": "533b5230-1b8f-11e8-9629-c7eca82aa7bd",
            "avatar": "https://resizing.flixster.com/EVAkglctn7E9B0hVKJrueplabuQ=/220x196/v1.cjs0NjYwNjtqOzE4NDk1OzEyMDA7MjIwOzE5Ng",
            "user": "Wendy",
            "text": "Tells exactly what happened.",
            "createdAt": "2020-07-16T19:48:42.481Z",
            "editedAt": "2020-07-16T19:48:47.481Z"
        },{
            "id": "80e00b40-1b8f-11e8-9629-c7eca82aa7b5",
            "userId": "61325",
            "avatar": "https://resizing.flixster.com/EVAkglctn7E9B0hVKJrueplabuQ=/220x196/v1.cjs0NjYwNjtqOzE4NDk1OzEyMDA7MjIwOzE5Ng",
            "user": "Wendy",
            "text": "Tells exactly what happened.",
            "createdAt": "2020-07-16T19:48:42.481Z",
            "editedAt": "2020-07-16T19:48:47.481Z"
        }
        ],
      myInform:{
        "userId": "61325",
        "avatar": "https://resizing.flixster.com/EVAkglctn7E9B0hVKJrueplabuQ=/220x196/v1.cjs0NjYwNjtqOzE4NDk1OzEyMDA7MjIwOzE5Ng",
        "user": "Wendy",
      }
    }

    addNewMessage = (data) =>{
      const myInform = this.state.myInform;
      const message = {
        "id":data.id,
        "userId":myInform.userId,
        "avatar":myInform.avatar,
        "user":myInform.user,
        "text":data.text,
        "createdAt":data.createdAt,
        "editedAt":""
      }
      this.setState({
        message:this.state.message.push(message)
      });
    }

  }



  render() {
    const data = this.state;
    return (
      <div className="pad-lr_chat">
        <Header 
          roomInfo = {data.roomInform}
        />
        <div className="chat-messageWndw marg-t_20px">
          {data.messages.length !== 0
            ?
              <MessageList
                messages={data.messages}
                myId={data.idUser}
              />
            :
              <Spiner/>
          }
        </div>
        <MessageInput/>
      </div>
    );
  }
}

export default Chat;
