import React from "react";
import { connect } from "react-redux";

function Modal(props) {
  console.log(props.editMsg.editMsg);
    return(
      props.editMsg.editMsg ? (
        <div className="modal flex-drctCol flex-spcarrnd flex-horCntr display-none">
          <h2>Edit message</h2>
          <input
            className="editMsg-input"
            type="textarea"
            name="Edit message"
            placeholder="Edit your message"
          />
          <div>
            <button className="editMsg-button" type="button">
              Ok
            </button>
            <button className="editMsg-button" type="button">
              Cancel
            </button>
          </div>
        </div>
      ) : null
    )
}

const mapStateToProps = (state) => {
  return {
    editMsg: state.chat.editMsg,
  };
};
const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
